import { SparksTheme } from '@bitdesign/sparks.sparks-theme';
import { Logo } from './logo.js';
import sparksLogo from './sparks.svg';

export const BasicLogo = () => {
  return (
    <SparksTheme>
      <Logo src={sparksLogo} />
    </SparksTheme>
  );
}

export const LogoWithName = () => {
  return (
    <SparksTheme>
      <Logo src={sparksLogo} name='Sparks' />
    </SparksTheme>
  );
}

export const LogoWithSlogan = () => {
  return (
    <SparksTheme>
      <Logo src={sparksLogo} name='Sparks' slogan='Design System' />
    </SparksTheme>
  );
}

export const LogoWithAlt = () => {
  return (
    <SparksTheme>
      <Logo src={sparksLogo} name='Sparks' slogan='Design System' alt='Sparks Design System' />
    </SparksTheme>
  );
}

export const LogoWithLink = () => {
  return (
    <SparksTheme>
      <Logo href='/' src={sparksLogo} name='Sparks' slogan='Design System' alt='Sparks Design System' />
    </SparksTheme>
  );
}
