import { render } from '@testing-library/react';
import { BasicLogo, LogoWithAlt, LogoWithName, LogoWithSlogan } from './logo.composition.js';

it('should render the logo image', () => {
  const { container } = render(<BasicLogo />);
  const rendered = container.querySelector('img');
  expect(rendered).toBeTruthy();
});

it('should render the name in bold', () => {
  const { container } = render(<LogoWithName />);
  const rendered = container.querySelector('b');
  const text = rendered.innerHTML;
  expect(text).toEqual('Sparks');
});

it('should render the slogan in a span', () => {
  const { container } = render(<LogoWithSlogan />);
  const rendered = container.querySelector('span');
  const text = rendered.innerHTML;
  expect(text).toEqual('Design System');
});

it('should include the name in the alt', () => {
  const { container } = render(<LogoWithName />);
  const rendered = container.querySelector('img');
  expect(rendered.alt).toEqual('Sparks');
});

it('should override name using the alt prop', () => {
  const { container } = render(<LogoWithAlt />);
  const rendered = container.querySelector('img');
  expect(rendered.alt).toEqual('Sparks Design System');
});
