import React from 'react';
import { render } from '@testing-library/react';
import { BasicHeader } from './header.composition.js';

it('should render the action text', () => {
  const { getByText } = render(<BasicHeader />);
  const rendered = getByText('Login');
  expect(rendered).toBeTruthy();
});

it('should render the logo', () => {
  const { getByText } = render(<BasicHeader />);
  const rendered = getByText('Sparks');
  expect(rendered).toBeTruthy();
});

it('should render its childs', () => {
  const { getByText } = render(<BasicHeader />);
  const rendered = getByText('Docs');
  expect(rendered).toBeTruthy();
});