import React from 'react';
import { render } from '@testing-library/react';
import { BasicLink } from './link.composition.js';

it('renders with the correct text', () => {
  const { getByText } = render(<BasicLink />);
  const rendered = getByText('Click here!');
  expect(rendered).toBeTruthy();
});

it('should render a native a tag', () => {
  const { container } = render(<BasicLink />);
  const rendered = container.querySelector('a');
  expect(rendered).toBeTruthy();
});
