import type { ReactNode } from 'react';
import { reactRouterAdapter } from '@teambit/ui-foundation.ui.navigation.react-router-adapter';
import {
  NavigationProvider as BaseNavigationProvider,
  RouterContextType,
} from '@teambit/base-react.navigation.link';

export type NavigationProviderProps = {
  /**
   * children of the provider.
   */
  children: ReactNode;

  /**
   * implementation for the navigation provider.
   * defaults to use `react-router`.
   * for other adapters see: https://bit.cloud/frontend
   */
  adapter?: RouterContextType;
};

export function NavigationProvider({
  children,
  adapter = reactRouterAdapter,
}: NavigationProviderProps) {
  return (
    <BaseNavigationProvider implementation={adapter}>
      {children}
    </BaseNavigationProvider>
  );
}
