import { CSSProperties, ReactNode } from 'react';
import classNames from 'classnames';
import { SparksThemeProvider } from './sparks-theme-provider.js';
import styles from './sparks-theme.module.scss';
import './reset.css';

export type SparksThemeProps = {
  /**
   * a root ReactNode for the component tree 
   * applied with the theme.
   */
  children?: ReactNode;
  /**
   * inject a class name to override to the theme.
   * this allows people to affect your theme. remove to avoid.
   */
  className?: string;

  /**
   * inject css properties.
   */
  style?: CSSProperties;
};

/**
 * a theme for the Acme organization.
 * it provides tokens, fonts and general styling for all components.
 */
export function SparksTheme({ children, ...rest }: SparksThemeProps) {
  return (
    <SparksThemeProvider.ThemeProvider
      {...rest}
      className={classNames(styles.sparksTheme, rest.className)}
    >
      {children}
    </SparksThemeProvider.ThemeProvider>
  );
}
