import { ComponentContext } from '@teambit/generator';
import { replace } from 'lodash';

export const docsFile = (context: ComponentContext) => {
  const { name, namePascalCase } = context;
  const spaced = replace(name, new RegExp('-', 'g'), ' ');

  return {
    relativePath: `${name}.docs.mdx`,
    content: `---
labels: ['${spaced}', 'app', 'app type']
description: 'A ${namePascalCase} app component.'
---

A basic Bit app component.

## Run the app

\`\`\`
# use the app
bit use ${name}

# run the app
bit run ${name}
\`\`\`

## Compose the app

Compose the app to create your own instance of this app.

\`\`\`ts
export default ${namePascalCase}.from({});
\`\`\`
`,
  };
};
