import { ComponentContext } from '@teambit/generator';

export const testFile = (context: ComponentContext) => {
  const { name, namePascalCase } = context;

  return {
    relativePath: `${name}.spec.ts`,
    content: `import { run${namePascalCase} } from './${name}.js';

it('should have a ${namePascalCase}.from() method', () => {
  expect(run${namePascalCase}).toBeTruthy();
});
`,
  };
};
