import { ComponentContext } from '@teambit/generator';

export const nodeAppFile = (context: ComponentContext) => {
  const { name } = context;

  return {
    relativePath: `${name}.bit-app.tsx`,
    content: `import { NodeServer } from '@bitdev/node.node-server';

export default NodeServer.from({
  name: '${name}',
  mainPath: import.meta.resolve('./${name}.app-root.js'),
});
`,
  };
};
