import { ComponentContext } from '@teambit/generator';

export const testFile = (context: ComponentContext) => {
  const { name, namePascalCase: Name, nameCamelCase } = context;

  return {
    relativePath: `${name}.spec.jsx`,
    content: `import { ${nameCamelCase} } from './${name}';

    test('should return hello world', () => {
      expect(${nameCamelCase}()).toEqual('hello world');
    });`,
  };
};
