import { ComponentContext } from '@teambit/generator';

export const componentFile = (context: ComponentContext) => {
  const { name, nameCamelCase } = context;
  return {
    relativePath: `${name}.ts`,
    content: `
/**
 * returns 'hello world'
 */
export function ${nameCamelCase}() {
  return 'hello world';
}
`,
  };
};
