import {
  ComponentContext,
  ComponentFile,
  ComponentTemplate,
} from '@teambit/generator';
import { indexFile } from './files/index-file';
import { componentFile } from './files/component';
import { testFile } from './files/test';
import { docsFile } from './files/docs';

// TODO: replace this with "import {ComponentTemplateOptions} from '@teambit/generator';" once v1.3 is released
export type NodeComponentTemplateOptions = Partial<
  Pick<ComponentTemplate, 'name' | 'description' | 'hidden' | 'env'>
>;

export class NodeModuleTemplate implements ComponentTemplate {
  constructor(
    readonly name = 'module',
    readonly description = 'create a node module',
    readonly hidden = false,
    readonly env?: string
  ) {}

  generateFiles(context: ComponentContext): ComponentFile[] {
    return [
      indexFile(context),
      componentFile(context),
      testFile(context),
      docsFile(context),
    ];
  }

  static from(options: NodeComponentTemplateOptions = {}) {
    return () =>
      new NodeModuleTemplate(
        options.name,
        options.description,
        options.hidden,
        options.env
      );
  }
}
