import { ComponentContext } from '@teambit/generator';

export const bitAppFile = (context: ComponentContext) => {
  const { name, namePascalCase } = context;
  return {
    relativePath: `${name}.bit-app.ts`,
    content: `import { ${namePascalCase} } from './${name}.js';

/**
 * use this file as a bootstrap for your app.
 */
runApp();
`,
  };
};

