import type { AppContext, Application } from "@teambit/application";
import { ComponentID } from '@teambit/component-id';
export type PlatformService<T extends PlatformServiceOptions = PlatformServiceOptions> = string | Promise<string> | [Promise<string>, T] | [string, T];
export interface PlatformServiceOptions {
    /**
     * name of the service.
     */
    name?: string;
    /**
     * use the remote url.
     */
    remoteUrl?: string;
    /**
     * force the use of the remote url.
     */
    forceRemote?: boolean;
}
export interface PlatformServiceModule {
    /**
     * application module of the platform component
     */
    module?: Application;
    /**
     * app context of the platform service component.
     */
    context?: AppContext;
    /**
     * url of the platform service component.
     */
    url?: string;
    /**
     * component ID of the platform service.
     */
    componentId: ComponentID;
    /**
     * package name of the platform component.
     */
    packageName: string;
    /**
     * name of the platform service.
     */
    name: string;
}
export interface PlatformServiceDef {
    port: number;
    serviceName: string;
}
