import type { PlatformService } from "./platform-service.js";

export interface PlatformLayer {
  /**
   * used as a gateway or when a single server is needed.
   */
  main: PlatformService;

  /**
   * services application
   */
  services?: PlatformService[];

  /**
   * port range to use for services.
   */
  portRange?: [number, number];

  /**
   * port range for the gateway.
   * by default uses the same port range as services.
   */
  mainPortRange?: [number, number];
}
