import type { Request, Response } from 'express';
export type RESTMethod = 'get' | 'post' | 'put' | 'delete' | 'trace' | 'options' | 'patch';
export type RESTRoute = {
    /**
     * method of the route.
     */
    method?: string;
    /**
     * path of the route.
     */
    path: string | RegExp;
    /**
     * route implementation
     */
    route: (req: Request, res: Response) => void;
};
/**
 * default REST route.
 */
export type DefaultRESTRoute = RESTRoute;
